import axios from "axios";
import React, { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import { useFormik } from "formik";
import * as Yup from "yup";
import ReactPaginate from "react-paginate";
import Header from "../../Layouts/Header";
import Sidebar from "../../Layouts/Sidebar";
import Toggle from "react-toggle";
import { useModal } from "react-hooks-use-modal";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./Toogle.css";
import { add, values } from "lodash";

const Category = () => {
  const [category, setcategory] = useState([]);
  const [page, setPage] = useState(1);

  const [modalType, setmodalType] = useState();
  const [value, setvalues] = useState({
    title: "",
    image: "",
  });

  const [Modal, open, close, isOpen] = useModal("root", {
    preventScroll: true,
    closeOnOverlayClick: false,
  });
  const openModal = (modaltype) => {
    open();
    setmodalType(modaltype);
  };

  const closeModal = (modaltype) => {
    close();
    setmodalType("");
  };

  const getUserList = () => {
    axios
      .get(
        `http://localhost:5050/category?current_page=${page.per_page}&per_page=${page.currentpage}`
      )
      .then((res) => {
        console.log(res.data);
        setPage(res.data);
        setcategory(res.data.user);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const handleChange = (event, value) => {
    setPage(value);
  };

  useEffect(() => {
    getUserList();
  }, []);

  // =======================add category===============================
  const addcategory = useFormik({
    initialValues: {
      title: "",
      image: "",
    },

    validationSchema: Yup.object({
      title: Yup.string()
        .max(15, "Must be 15 characters or less")
        .required("Required"),
      image: Yup.mixed().required(),
    }),

    onSubmit: async ({ resetForm, title, image }) => {
      try {
        // setSubmitting(false);
        var formdata = new FormData();
        formdata.append("title", title);
        formdata.append("image", image);
        const option = {
          headers: { "Content-Type": "multipart/form-data" },
        };

        await axios
          .post("http://localhost:5050/category", formdata, option)
          .then((res) => {
            //resetForm({});
            close();
            toast.success("Category Added Successfully");
          });
