import axios from "axios";
import React, { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import ReactPaginate from "react-paginate";
import Header from "../../Layouts/Header";
import Sidebar from "../../Layouts/Sidebar";

const Category = () => {
  const [category, setcategory] = useState([]);
  const [page, setPage] = useState(1);
  const countPerPage = 3;

  const columns = [
    {
      name: "Avatar",
      cell: (row) => (
        <img
          src={`http://localhost:5050/images/${category.image}`}
          style={{ width: "50px" }}
        />
      ),
    },
    {
      name: "Title",
      selector: "",
    },
    {
      name: "Image",
      selector: "Image",
    },
    {
      name: "Email",
      selector: "email",
    },
  ];

  const getUserList = () => {
    axios
      .get(
        `http://localhost:5050/category?current_page=${page}&per_page=${countPerPage}`
      )
      .then((res) => {
        console.log(res.data);
        setcategory(res.data.user);
      })
      .catch((err) => {
        console.log(err);
      });
  };


  useEffect(() => {
    getUserList();
  }, [page]);
  return (
    <>
      <Header />
      <Sidebar />
      <div className="content-wrapper">
        <div className="box">
          <div className="box-header">
            <h3 className="box-title">Category</h3>
            <button
              className="btn btn-info"
              // data-toggle="modal"
              //   data-target="#modal-info"
              style={{
                float: "right",
                padding: "0px 42px 2px",
                fontSize: "20px",
              }}
              //   onClick={() => openModal("add")}
            >
              Add User
            </button>
          </div>

          <div class="box-body">
            <table id="example1" class="table table-bordered table-striped">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Image</th>
                  <th>status</th>
                  <th>Action</th>
                </tr>
              </thead>
              {console.log("88888888888888888***********", category)}
              {category.map((data) => {
                console.log(data, "&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&&");
                return (
                  <tbody>
                    <tr>
                      <td>{data.title}</td>
                      <td>
                        <img
                          src={`http://localhost:5050/images/${data.image}`}
                          style={{ width: "50px" }}
                        />
                      </td>
                      <td>{data.status}</td>
                      <td>
                        <button
                          type="button"
                          class="btn btn-success"
                          data-toggle="modal"
                          data-target="#modal-success"
                          //onClick={() => onSubmit(user._id)}
                        >
                          EDIT
                        </button>
                        <button
                          type="button"
                          class="btn btn-danger"
                          //onClick={() => user_delete(user._id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  </tbody>
                );
              })}
              <DataTable
                // title="Employees"
                //columns={columns}
                data={category.user}
                highlightOnHover
                pagination
                paginationServer
                //  paginationTotalRows={users.total}
                paginationPerPage={countPerPage}
                paginationComponentOptions={{
                  noRowsPerPage: true,
                }}
                onChangePage={(page) => setPage(page)}
              />

              {/* <ReactPaginate
                previousLabel={"← Previous"}
                nextLabel={"Next →"}
                pageCount={countPerPage}
                onPageChange={handlePageClick}
                containerClassName={"pagination"}
                previousLinkClassName={"pagination__link"}
                nextLinkClassName={"pagination__link"}
                disabledClassName={"pagination__link--disabled"}
                activeClassName={"pagination__link--active"}
              /> */}
            </table>
          </div>
        </div>
      </div>
    </>
  );
};

export default Category;
