import axios from "axios";
import React, { useEffect, useState } from "react";
import DataTable from "react-data-table-component";
import { useFormik } from "formik";
import * as Yup from "yup";
import ReactPaginate from "react-paginate";
import Header from "../../Layouts/Header";
import Sidebar from "../../Layouts/Sidebar";
import Toggle from "react-toggle";
import { useModal } from "react-hooks-use-modal";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./Toogle.css";
import { add, values } from "lodash";

const Category = () => {
  const [category, setcategory] = useState([]);
  const [page, setPage] = useState(1);

  const countPerPage = 2;

  const [modalType, setmodalType] = useState();

  const [Modal, open, close, isOpen] = useModal("root", {
    preventScroll: true,
    closeOnOverlayClick: false,
  });
  const openModal = (modaltype) => {
    open();
    setmodalType(modaltype);
  };

  const closeModal = (modaltype) => {
    close();
    setmodalType("");
  };

  const getUserList = () => {
    axios
      .get(
        `http://localhost:5050/category?current_page=${page.per_page}&per_page=${page.currentpage}`
      )
      .then((res) => {
        console.log(res.data);
        setPage(res.data);
        setcategory(res.data.user);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const handleChange = (event, value) => {
    setPage(value);
  };

  useEffect(() => {
    getUserList();
  }, []);

  // =======================add category===============================
  const addcategory = useFormik({
    initialValues: {
      title: "",
      image: "",
    },

    validationSchema: Yup.object({
      title: Yup.string()
        .max(15, "Must be 15 characters or less")
        .required("Required"),
      image: Yup.mixed().required(),
    }),

    onSubmit: async ({ resetForm, title, image }) => {
      try {
        // setSubmitting(false);
        var formdata = new FormData();
        formdata.append("title", title);
        formdata.append("image", image);
        const option = {
          headers: { "Content-Type": "multipart/form-data" },
        };

        await axios
          .post("http://localhost:5050/category", formdata, option)
          .then((res) => {
            //resetForm({});
            close();
            toast.success("Category Added Successfully");
          });
      } catch (err) {
        console.log(err);
        toast.error(err.response.data.message);
      }
    },
  });


  toogleupdate

  return (
    <>
      <Header />
      <Sidebar />
      <div className="content-wrapper">
        <div className="box">
          <div className="box-header">
            <h3 className="box-title">Category</h3>
            <button
              className="btn btn-info"
              // data-toggle="modal"
              //   data-target="#modal-info"
              style={{
                float: "right",
                padding: "0px 42px 2px",
                fontSize: "20px",
              }}
              onClick={() => openModal("add")}
            >
              Add User
            </button>
          </div>

          <div class="box-body">
            <table id="example1" class="table table-bordered table-striped">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Image</th>
                  <th>status</th>
                  <th>Action</th>
                </tr>
              </thead>

              {category.map((data) => {
                return (
                  <tbody>
                    <tr>
                      <td>{data.title}</td>
                      <td>
                        <img
                          src={`http://localhost:5050/images/${data.image}`}
                          style={{ width: "50px" }}
                        />
                      </td>
                      <td>
                        <Toggle
                          id="cheese-status"
                          name="sunday"
                          value={data.status}
                          checked={data.status}
                          onChange={""}
                        />
                      </td>
                      <td>
                        <button
                          type="button"
                          class="btn btn-success"
                          data-toggle="modal"
                          data-target="#modal-success"
                          //onClick={() => onSubmit(user._id)}
                        >
                          EDIT
                        </button>
                        <button
                          type="button"
                          class="btn btn-danger"
                          //onClick={() => user_delete(user._id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  </tbody>
                );
              })}

              <ReactPaginate
                previousLabel={"← Previous"}
                nextLabel={"Next →"}
                pageCount={page.current_page}
                onPageChange={() => setPage()}
                containerClassName={"pagination"}
                previousLinkClassName={"pagination__link"}
                nextLinkClassName={"pagination__link"}
                disabledClassName={"pagination__link--disabled"}
                activeClassName={"pagination__link--active"}
              />
            </table>
          </div>
        </div>

        <Modal>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <button type="button" className="close" onClick={close}>
                  <span aria-hidden="true">×</span>
                </button>
                <h4 className="modal-title">Add Category</h4>
              </div>
              <div
                className="modal-body"
                style={{
                  height: "286px",
                  backgroundColor: "#d0d5d6 !important",
                }}
              >
                <form onSubmit={addcategory.handleSubmit}>
                  <div className="form-group has-feedback">
                    <input
                      type="text"
                      className="form-control"
                      placeholder="Title"
                      id="title"
                      name="title"
                      onChange={addcategory.handleChange}
                      onBlur={addcategory.handleBlur}
                      value={addcategory.values.title}
                    />
                    {addcategory.touched.title && addcategory.errors.title ? (
                      <div style={{ color: "red" }}>
                        {addcategory.errors.tile}
                      </div>
                    ) : null}
                    <span className="glyphicon glyphicon-user form-control-feedback" />
                  </div>
                  <div className="form-group has-feedback">
                    <input
                      type="file"
                      className="form-control"
                      placeholder="image"
                      id="image"
                      name="image"
                      onChange={(e) => {
                        addcategory.setFieldValue("image", e.target.files[0]);
                      }}
                      onBlur={addcategory.handleBlur}
                      value={addcategory.values.file}
                    />
                    {addcategory.touched.image && addcategory.errors.image ? (
                      <div style={{ color: "red" }}>
                        {addcategory.errors.image}
                      </div>
                    ) : null}
                    <span className="glyphicon glyphicon-envelope form-control-feedback" />
                  </div>
                  {/* <div className="form-group has-feedback">
                    <input
                      type="password"
                      className="form-control"
                      placeholder="Password"
                      id="password"
                      name="password"
                      onChange={formik.handleChange}
                      onBlur={formik.handleBlur}
                      value={formik.values.password}
                    />
                    {formik.touched.password && formik.errors.password ? (
                      <div style={{ color: "red" }}>
                        {formik.errors.password}
                      </div>
                    ) : null}
                    <span className="glyphicon glyphicon-lock form-control-feedback" />
                  </div> */}
                  <>
                    <div className="col-xs-4">
                      <div className="pull-left">
                        <button
                          type="submit"
                          className="btn btn-primary btn-block btn-flat"
                        >
                          Submit
                        </button>
                      </div>
                      <div className="pull-right">
                        {/* <button
                            type="submit"
                            className="btn btn-primary btn-block btn-flat"
                            onClick={close}
                          >
                            Close
                          </button> */}
                      </div>
                    </div>
                  </>
                </form>
              </div>
            </div>
          </div>
        </Modal>
      </div>
      <ToastContainer />
    </>
  );
};

export default Category;
